INSERT INTO [dbo].[Games] ([Id], [Name], [Publisher], [ShortDescription], [Description], [Price], [ImageData], [ReleaseDate], [DateOfAdding], [OnSale], [CategoryId]) VALUES (8, N'Grand Theft Auto V [GTA 5]', N'Rockstar', N'Grand Theft Auto V на Playstation 4 – продолжение криминального боевика с сатирой на современную действительность.', N'Grand Theft Auto V на Playstation 4 – продолжение криминального боевика с сатирой на современную действительность. В этот раз история возвращается в город Лос-Сантос,  прообразом которого послужил Лос-Анджелес. Главных героя сразу три: Майкл, Франклин и Тревор. У каждого свои умения и особенности.', CAST(2590.00 AS Decimal(15, 2)), <Binary Data>, N'2020', N'2020-05-08 12:35:29', 1, 1)
INSERT INTO [dbo].[Games] ([Id], [Name], [Publisher], [ShortDescription], [Description], [Price], [ImageData], [ReleaseDate], [DateOfAdding], [OnSale], [CategoryId]) VALUES (12, N'FIFA 21', N'Electronic Arts', N'Серия компьютерных игр в жанре симулятора футбола, которая разрабатывается студией EA Canada, входящей в состав корпорации Electronic Arts', N'Что готовит нам сезон грядущий? EA SPORTS – дружная команда поклонников спорта и видеоигр, которые из раза в раз стараются перенести бесценный опыт соревновательного духа на виртуальное пространство,  делая его доступным практически каждому! И вот, теперь и вы можете купить FIFA 21 на базе движка Frostbite, став героем множества турниров, будь то UEFA Champions League или CONMEBOL Libertadores! Сплотите команду мечты и хозяйничайте на игровых полях улиц и стадионов!Вы – сердце этого зрелищного спорта!', CAST(2150.00 AS Decimal(15, 2)), <Binary Data>, N'2020', N'2019-04-07 13:00:00', 1, 2)
INSERT INTO [dbo].[Games] ([Id], [Name], [Publisher], [ShortDescription], [Description], [Price], [ImageData], [ReleaseDate], [DateOfAdding], [OnSale], [CategoryId]) VALUES (14, N'Fallout 76', N'Bethesda Softworks', N'Действие происходит после ядерной войны на территории США, которая превратилась в радиоактивную пустыню и охвачена анархией.', N'Западная Вирджиния вновь становится обителью разумной жизни! Апокалипсис превратил некогда прекрасный штат в пустошь, но это не повод унывать для его обитателей! Решившись купить Fallout 76 Wastelanders и встретить негодяев и мирных жителей, вы откроете для себя совершенно новую главу в истории серии игр Fallout! Впрочем, вам нет нужды встречать все испытания и невзгоды в одиночку, ибо вы можете позвать с собой друзей, демонстрируя завидную взаимовыручку, когда дело касается отстрела мутировавшей фауны и людей, чьи моральные нормы тоже прошли своего рода мутацию!', CAST(735.00 AS Decimal(15, 2)), <Binary Data>, N'2018', N'2020-04-20 16:00:01', 1, 3)
INSERT INTO [dbo].[Games] ([Id], [Name], [Publisher], [ShortDescription], [Description], [Price], [ImageData], [ReleaseDate], [DateOfAdding], [OnSale], [CategoryId]) VALUES (19, N'Wasteland 3', N'inXile Entertainment', N'Радиоактивные пустоши взывают к вам! Это продолжение серии ролевых игр, положивших начало жанру постапокалипсиса в видеоиграх', N'Радиоактивные пустоши взывают к вам! Высоко оцененная серия ролевых игр от inXile Entertainment продолжает своё победоносное шествие, выпустив третью часть занимательной вселенной постапокалипсиса. Решившись купить Wasteland 3 и повлиять на судьбы многих, на судьбу всей Аризоны. Вам придётся посетить раскаленные пустыни, холодные горы, потерять всё и даже построить новую базу. Собственными руками вы построите нечто, что станет маяком безопасности и надёжности в опасном мире, полный опасных фракций, интриг, сумасшедших культистов, банд головорезов и хищников!', CAST(1599.00 AS Decimal(15, 2)), <Binary Data>, N'28 августа 2020', N'2020-04-21 17:20:00', 1, 4)
