$(document).ready(function(){$("#formAdd").submit(function(n){n.preventDefault();$(".error").remove();$.ajax({url:"/admin/uploadpicturefilebrowser",type:"POST",data:new FormData(this),processData:!1,contentType:!1,success:function(n){$("#uploadPicture").val("");$("#fileExplorer").append(`<div class="thumbnail"><img class="ml-2" src="/images/uploads/${n.data}" alt="thumb" title="${n.data}"
                                                width="136" height="80" /><br />${n.data}<form action="/admin/deleteimage" method="post">
                                                <input type="hidden" name="name" value="${n.data}"/><input style="margin-left:30px;" type="submit" value="Удалить"/></form></div>`)},error:function(){$('<div class="error ml-4"/>').text("Недопустимый формат").insertBefore("#formAdd")}})})});